from pprint import pprint

def rowFormat(matrix):
	end = ''
	for i in range(len(matrix)):
		if(i % 2 == 1):
			matrix[i] = matrix[i][::-1]
		end += ','.join(matrix[i]) + ','
	return end[:-1].replace('\'','')


def toMatrix(str):
	numbers = str.split(',')
	rows = []
	for i in range(17):
		n = (i) * 17
		rows.append(numbers[i * 17: (i + 1) * 17])
	return rows

